contracts/vc/OpenCampusCertificateNFTMin.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import {AccessControl} from "@animoca/ethereum-contracts/contracts/access/AccessControl.sol";
import {ContractOwnership} from "@animoca/ethereum-contracts/contracts/access/ContractOwnership.sol";
import {IIssuersDIDRegistry} from "./interfaces/IIssuersDIDRegistry.sol";
import {CertificateNFTv1MetaData} from "./libraries/CertificateNFTv1MetaData.sol";
import {OpenCampusCertificateNFTv1} from "./OpenCampusCertificateNFTv1.sol";

contract OpenCampusCertificateNFTMinter is AccessControl {
    IIssuersDIDRegistry internal immutable DID_REGISTRY;
    OpenCampusCertificateNFTv1 internal immutable NFT_V1;
    /// @notice Thrown when the signature is invalid for the NFT payload.
    error InvalidSignature();

    /// @notice Thrown when the issuer is not one of the allowed issuers.
    error IssuerNotAllowed();

    constructor(IIssuersDIDRegistry didRegistry, OpenCampusCertificateNFTv1 nftv1) ContractOwnership(msg.sender) {
        DID_REGISTRY = didRegistry;
        NFT_V1 = nftv1;
    }

    /// @dev signature is ECDSA signature for (to, tokenId, metadata)
    /// @dev signature is a 65 bytes raw signature without compacting
    function mint(address to, uint256 tokenId, CertificateNFTv1MetaData.MetaData calldata metadata, bytes calldata signature) external {
        // recover the signer
        if (signature.length != 65) revert InvalidSignature();

        uint8 v;
        bytes32 r;
        bytes32 s;
        assembly {
            r := calldataload(signature.offset)
            s := calldataload(add(signature.offset, 0x20))
            v := calldataload(add(signature.offset, 0x21))
        }

        // Use the native ecrecover provided by the EVM
        address signer = ecrecover(keccak256(abi.encode(to, tokenId, metadata)), v, r, s);
        bytes32 hashedDid = keccak256(abi.encodePacked(metadata.issuerDid));

        if (DID_REGISTRY.issuers(hashedDid, signer)) {
            NFT_V1.mint(to, tokenId, metadata);
        } else {
            revert IssuerNotAllowed();
        }
    }
}

contracts/vc/OpenCampusCertificateNFTv1..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import {AccessControl} from "@animoca/ethereum-contracts/contracts/access/AccessControl.sol";
import {ITokenMetadataResolver} from "@animoca/ethereum-contracts/contracts/token/metadata/interfaces/ITokenMetadataResolver.sol";
import {ERC721Metadata} from "@animoca/ethereum-contracts/contracts/token/ERC721/ERC721Metadata.sol";
import {ContractOwnership} from "@animoca/ethereum-contracts/contracts/access/ContractOwnership.sol";
import {ERC721Storage} from "@animoca/ethereum-contracts/contracts/token/ERC721/libraries/ERC721Storage.sol";
import {AccessControlStorage} from "@animoca/ethereum-contracts/contracts/access/libraries/AccessControlStorage.sol";
import {IERC721} from "@animoca/ethereum-contracts/contracts/token/ERC721/interfaces/IERC721.sol";
import {CertificateNFTv1MetaData} from "./libraries/CertificateNFTv1MetaData.sol";

contract OpenCampusCertificateNFTv1 is IERC721, ERC721Metadata, AccessControl {
    using ERC721Storage for ERC721Storage.Layout;
    using AccessControlStorage for AccessControlStorage.Layout;
    using CertificateNFTv1MetaData for CertificateNFTv1MetaData.MetaData;

    bytes32 public constant MINTER_ROLE = "minter";
    mapping(uint256 => CertificateNFTv1MetaData.MetaData) public vcData;

    /// @notice Thrown when any transfer functions are called but not allowed.
    error TransferNotAllowed();

    /// @notice Thrown when any operator related methods are called.
    error NoOperatorAllowed();

    constructor(
        string memory tokenName,
        string memory tokenSymbol,
        ITokenMetadataResolver metadataResolver
    ) ContractOwnership(msg.sender) ERC721Metadata(tokenName, tokenSymbol, metadataResolver) {
        ERC721Storage.initERC721Mintable();
    }

    /// @dev Reverts with `NotRoleHolder` if the sender does not have the 'minter' role.
    function mint(address to, uint256 tokenId, CertificateNFTv1MetaData.MetaData calldata metadata) external {
        AccessControlStorage.layout().enforceHasRole(MINTER_ROLE, msg.sender);

        ERC721Storage.layout().mint(to, tokenId);
        vcData[tokenId] = metadata;
    }

    /// @inheritdoc IERC721
    function balanceOf(address owner) external view returns (uint256 balance) {
        return ERC721Storage.layout().balanceOf(owner);
    }

    /// @inheritdoc IERC721
    function ownerOf(uint256 tokenId) external view returns (address tokenOwner) {
        return ERC721Storage.layout().ownerOf(tokenId);
    }

    /// @inheritdoc IERC721
    function getApproved(uint256 tokenId) external view returns (address approved) {
        revert NoOperatorAllowed();
    }

    /// @inheritdoc IERC721
    function approve(address to, uint256 tokenId) external virtual {
        revert TransferNotAllowed();
    }

    /// @inheritdoc IERC721
    function setApprovalForAll(address operator, bool approved) external virtual {
        revert NoOperatorAllowed();
    }

    /// @inheritdoc IERC721
    function transferFrom(address from, address to, uint256 tokenId) external {
        revert TransferNotAllowed();
    }

    /// @inheritdoc IERC721
    function safeTransferFrom(address from, address to, uint256 tokenId) external virtual {
        revert TransferNotAllowed();
    }

    /// @inheritdoc IERC721
    function safeTransferFrom(address from, address to, uint256 tokenId, bytes calldata data) external virtual {
        revert TransferNotAllowed();
    }

    /// @inheritdoc IERC721
    function isApprovedForAll(address owner, address operator) external view returns (bool approvedForAll) {
        revert NoOperatorAllowed();
    }
}

contracts/vc/OpenCampusIssuersDIDRegistr.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import {AccessControl} from "@animoca/ethereum-contracts/contracts/access/AccessControl.sol";
import {ContractOwnership} from "@animoca/ethereum-contracts/contracts/access/ContractOwnership.sol";
import {AccessControlStorage} from "@animoca/ethereum-contracts/contracts/access/libraries/AccessControlStorage.sol";
import {IIssuersDIDRegistry} from "./interfaces/IIssuersDIDRegistry.sol";
import {IssuerAdded, IssuerRemoved} from "./events/IssuersDIDRegistryEvents.sol";

/// @title OpenCampusIssuersDIDRegistry.
/// @notice A registry storing the valid issusers based on eth address.
contract OpenCampusIssuersDIDRegistry is AccessControl, IIssuersDIDRegistry {
    using AccessControlStorage for AccessControlStorage.Layout;

    bytes32 public constant OPERATOR_ROLE = "operator";
    mapping(bytes32 => mapping(address => bool)) public issuers;

    /// @notice Thrown when issuer input is invalid.
    error InvalidIssuer();

    /// @notice Thrown when a given did and issuerAddress relationship does not exist.
    error RelationshipDoesNotExist();

    constructor() ContractOwnership(msg.sender) {}

    /// @dev Reverts with `NotRoleHolder` if sender does not have `operator` role.
    /// @dev Reverts with `InvalidIssuer` if `did` is empty or `issuerAddress` is zero.
    /// @dev Emits a {IssuerAdded} event when an issuer is added.
    /// @param did DID of the issuer.
    /// @param issuerAddress The Eth address of the issuer.
    function addIssuer(string calldata did, address issuerAddress) external {
        AccessControlStorage.layout().enforceHasRole(OPERATOR_ROLE, msg.sender);

        if (bytes(did).length == 0 || issuerAddress == address(0)) {
            revert InvalidIssuer();
        }

        bytes32 hashedDid = keccak256(abi.encodePacked(did));
        issuers[hashedDid][issuerAddress] = true;
        emit IssuerAdded(hashedDid, issuerAddress, msg.sender);
    }

    /// @dev Reverts with `NotRoleHolder` if sender does not have `operator` role.
    /// @dev Reverts with `RelationshipDoesNotExist` if relationship does not exist between the given did and issuerAddress
    /// @dev Emits a {IssuerRemoved} event when an issuer is removed.
    /// @param did DID of the issuer to be removed.
    /// @param issuerAddress The Eth address of the issuer.
    function removeIssuer(string calldata did, address issuerAddress) external {
        AccessControlStorage.layout().enforceHasRole(OPERATOR_ROLE, msg.sender);
        bytes32 hashedDid = keccak256(abi.encodePacked(did));
        if (!issuers[hashedDid][issuerAddress]) {
            revert RelationshipDoesNotExist();
        }
        delete issuers[hashedDid][issuerAddress];
        emit IssuerRemoved(hashedDid, issuerAddress, msg.sender);
    }

    /// @dev returns true if the issuer with given Eth Address and did association is included in this registry
    /// @param did The hashed value of the issuerDid
    /// @param issuerAddress The Eth address of the issuer
    function isIssuerAllowed(string calldata did, address issuerAddress) external view returns (bool allowed) {
        bytes32 hashedDid = keccak256(abi.encodePacked(did));
        return issuers[hashedDid][issuerAddress];
    }
}

